import React, {useState  } from 'react'

import axios from 'axios'
import { Button ,Modal } from "react-bootstrap";

/**
* @author
* @function Admin
**/


const Admin = ({movie}) => {

  const [newMovie,handelMovie]=useState([{name:"",imdbRating:"",category:"", image:""}])
// delete Movie
  const deleteMovie= (id)=>{

    axios.delete(`http://localhost:4000/posts/${id}`)
    .then(response=> console.log(response) )
    .catch(err=>err)

  }
  const handelChange = (e)=>{

    const {name,value}= e.target
    //SettingsInputAntenna({...addedmovie,[name]:value})
    handelMovie({...newMovie,[name]:value})
    console.log("hhhhhhh", newMovie)



    }
  // Add movie
  const addMovie=(e)=>{
    e.preventDefault()
    axios.post("http://localhost:4000/posts",newMovie)
    .then(response=> console.log(response) )
    .catch(err=>err)



    }
    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  return(
    <div>
    <span classeName = "pageTitle">Movies</span>


          <div className="AddMovie">
                <h1>Add Movie modal</h1>
                <Button variant="primary" onClick={handleShow}>
        Add a movie
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>

        </Modal.Header>
        <Modal.Body>

        <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Control type="url" 
              name="image"
              value={newMovie.image}
              onChange={(e) =>
                handelMovie({ ...newMovie, Poster: e.target.value })
              } placeholder="Poster of the movie / serie" />
        </Form.Group>
        <Form.Group>
          <Form.Control type="text" name="Title"
              value={details.Title}
              onChange={(e) =>
                setDetails({ ...details, Title: e.target.value })
              } placeholder="Title of the movie / serie" />
        </Form.Group>
        <Form.Group>
          <Form.Control type="text" name="Plot"
              value={details.Plot}
              onChange={(e) =>
                setDetails({ ...details, Plot: e.target.value })
              }  placeholder="Plot of the movie / serie" />
        </Form.Group>
        <Form.Group>
          <Form.Control type="number" name="imdbRating"
              value={details.imdbRating}
              onChange={(e) =>
                setDetails({ ...details, imdbRating: e.target.value })
              }  placeholder="imdbRating of the movie / serie" min="1" max="10" step="0.1"/>
        </Form.Group>
        <Form.Group>
          <Form.Control type="text" name="Type"
              value={details.Type}
              onChange={(e) =>
                setDetails({ ...details, Type: e.target.value })
              }  placeholder="Type of the movie / serie"/>
        </Form.Group>  
        <Form.Group>
          <Form.Control type="text" name="Genre"
              value={details.Genre}
              onChange={(e) =>
                setDetails({ ...details, Genre: e.target.value })
              }  placeholder="Genre of the movie / serie" />
        </Form.Group>
        <Form.Group>
          <Form.Control type="number" name="Year"
              value={details.Year}
              onChange={(e) =>
                setDetails({ ...details, Year: e.target.value })
              }  placeholder="Year of the movie / serie" />
        </Form.Group>
        <Form.Group>
          <Form.Control type="text" name="Runtime"
              value={details.Runtime}
              onChange={(e) =>
                setDetails({ ...details, Runtime: e.target.value })
              }  placeholder="Runtime of the movie / serie" />
        </Form.Group>
        <Form.Group>
          <Form.Control type="number" name="Rating"
              value={details.Rating}
              onChange={(e) =>
                setDetails({ ...details, Rating: e.target.value })
              }  placeholder="Rating of the movie / serie" min="1" max="5" />
        </Form.Group> 
         <div className="d-flex justify-content-center">
           <Button className="btn-icon" type="submit"><FaPlusCircle className="icon"/></Button>
         </div>
      </Form>



        </Modal.Body>

      </Modal>

            </div>


    {movie.map(el=>
      <div className="movie">
        <h1>{el.name}</h1>
        <h2>{el.imdbRating}</h2>
        <h3>{el.category}</h3>
        <h3>{el.id}</h3>
        <button onClick={()=>deleteMovie(el.id)}>Supprimer</button>
      </div>

      )}






</div>
   )

 }

export default Admin
