import React, {useState , useEffect } from 'react'

import axios from 'axios'

/**
* @author
* @function Admin
**/


const Admin = ({movie}) => {

  const [newMovie,handelMovie]=useState([{name:"",imdbRating:"",category:""}])
// delete Movie
  const deleteMovie= (id)=>{

    axios.delete(`http://localhost:4000/posts/${id}`)
    .then(response=> console.log(response) )
    .catch(err=>err)

  }
  const handelChange = (e)=>{

    const {name,value}= e.target
    //SettingsInputAntenna({...addedmovie,[name]:value})
    handelMovie({...newMovie,[name]:value})
    console.log("hhhhhhh", newMovie)



    }
  // Add movie
  const addMovie=(e)=>{
    e.preventDefault()
    axios.post("http://localhost:4000/posts",newMovie)
    .then(response=> console.log(response) )
    .catch(err=>err)



    }
  return(
    <div>
    <span classeName = "pageTitle">Movies</span>


    <div className="AddMovie">
                <h1>Haya nzidou</h1>

                <form type ="submit" onSubmit={addMovie}>
                  <input onChange={handelChange} name ="name"></input>
                  <input onChange={handelChange} name = "imdbRating"></input>
                  <input onChange={handelChange} name = "category"></input>
                  <button type= "submit" > Ajouter un film</button>
                </form>




            </div>


    {movie.map(el=>
      <div className="movie">
        <h1>{el.name}</h1>
        <h2>{el.imdbRating}</h2>
        <h3>{el.category}</h3>
        <h3>{el.id}</h3>
        <button onClick={()=>deleteMovie(el.id)}>Supprimer</button>
      </div>

      )}






</div>
   )

 }

export default Admin
